import React, { useState } from 'react';
import { makeStyles } from "@material-ui/core/styles";
// import { MenuList } from '@material-ui/core';
// import ListItem from "@material-ui/core/ListItem";
import Box from "@material-ui/core/Box";
import Grid from "@material-ui/core/Grid";
import Card from "@material-ui/core/Card";
import CardActionArea from "@material-ui/core/CardActionArea";
import CardActions from "@material-ui/core/CardActions";
import CardContent from "@material-ui/core/CardContent";
import CardMedia from "@material-ui/core/CardMedia";
import Button from "@material-ui/core/Button";
import Typography from "@material-ui/core/Typography";
// import { NavLink } from 'react-router-dom'




const useStyles = makeStyles((theme) => ({
  mainContainer: {
    background: "#233",
    height: "100%",
  },
  cardContainer: {
    maxWidth: 345,
    margin: "3rem auto",
  },
  // projectsMenu:{
  //   color:"white",
  //   fontSize:"14px",
  //   padding:"1rem",
  //   margin:"1rem",
  //   textDecoration:"none", 
  //},
  subMenuGrid:{
    textAlign:"center",
      padding:"1rem",
      background:"#2D2926FF",
  }
}));




const Portfolio = (props) => {

  const classes = useStyles();

  return (
    <Box component="div" className={classes.mainContainer}>

        <Grid className={classes.subMenuGrid}>
                    <button value="All" onClick={this.props.handalbtns}>All</button>
                    <button value="Mobile" onClick={this.props.handalbtns}>Mobile</button>
                    <button value="Web" onClick={this.props.handalbtns}>Web/Window</button>
                    <button value="UX" onClick={this.props.handalbtns}>UX/UI</button>
                  </Grid>
      <Grid container justify="center">
        {/* Projects */}
           {this.props.projects.map((project) => (
              <Grid item xs={12} sm={8} md={4} key={project.id}>
                <Card className={classes.cardContainer}>
                  <CardActionArea>
                    <CardMedia
                      component="img"
                      alt="Project 1"
                      height="140"
                      image={this.props.project.image}
                    />
                    <CardContent>
                      <Typography variant="h5" gutterBottom>
                        {this.props.project.name}
                      </Typography>
                      <Typography variant="body2" color="textSecondary">
                        {this.props.project.description}
                      </Typography>
                    </CardContent>
                  </CardActionArea>
                  <CardActions>
                    <Button size="small" color="primary">
                      Share
                    </Button>
                    <Button size="small" color="primary">
                      Live Demo
                    </Button>
                  </CardActions>
                </Card>
              </Grid>
        ))}
      {/* ..................second project....................* */}

      </Grid>
    </Box>
  );
};

export default Portfolio;
