import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import { Container, Row, Col } from 'reactstrap';
import "./skills.css";



const Progress = ({done}) => {
	const [style, setStyle] = React.useState({});

	setTimeout(() => {
		const newStyle = {
			opacity: 1,
			width: `${done}%`
		}

		setStyle(newStyle);
	}, 200);

	return (
		<div className="progress">
			<div className="progress-done" style={style}>
				{done}%
			</div>
		</div>
	)
}



const Skills = () => {

const classes = useStyles();

    return (

        <Container className={classes.background}>
                <Row className={classes.skillTitle}><h2>My Skills</h2></Row>
           <Row class={classes.AllTextCenter}>

                <Row className={classes.flexAligh}>
                    <Col className={classes.skillItems}>

                      <div>
                         <h3>HTML5 / CSS3</h3>
                        <Progress done="70"/>
                      </div>

                    </Col>
                    <Col className={classes.skillItems}>
                      <h3>Bootstrap</h3>

                    </Col>
                </Row>  


           </Row>   
        </Container>                        

    );
  };
  export default Skills;


